import React, { useState } from 'react'

const Signup = () => {
    const [credentials, setCredentials] = useState({ username: "", email: "", password: "", cpassword: "" });

    const handleSubmit = async (e) => {
        e.preventDefault();
        const { username, email, password, cpassword } = credentials;
        const response = await fetch('http://localhost:5000/api/auth/crusereate', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
                // 'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjQyM2YzNzYwMjRmNDRhN2M3ZWNkZTM1In0sImlhdCI6MTY4MDA4NDY4M30.K3_5HXaLuY5Umx53XiDG-zT1hSATr-q1I9THernuxrY' 
            },
        body: JSON.stringify({ username: credentials.username, email: credentials.email, password: credentials.password })
    });
    const json = await response.json();
    console.log(json);

    if (json.success) {
        // Save the auth token and redirect
        localStorage.setItem('token', json.authtoken);
        history.push("/");
    }
    else {
        alert("Invalid credentials");
    }
}

const onChange = (e) => {
    // console.log(e.target.email);
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
}

return (
    <div className='container'>
        <form onSubmit={handleSubmit}>
            <div className="mb-3">
                <label htmlFor="username" className="form-label">Name</label>
                <input type="username" className="form-control" id="username" name="username" onChange={onChange} />
                {/* <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div> */}
            </div>
            <div className="mb-3">
                <label htmlFor="email" className="form-label">Email address</label>
                <input type="email" className="form-control" id="email" name="email" onChange={onChange} aria-describedby="emailHelp" />
                {/* <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div> */}
            </div>
            <div className="mb-3">
                <label htmlFor="password" className="form-label">Password</label>
                <input type="password" className="form-control" name="password" onChange={onChange} id="password" />
            </div>
            <div className="mb-3">
                <label htmlFor="cpassword" className="form-label">Confirm Password</label>
                <input type="cpassword" className="form-control" name="cpassword" onChange={onChange} id="cpassword" />
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    </div>
)
}

export default Signup
